    float sigmak;               /**< k-smoothing */

	int octave_width ;          /**< current octave width. */
	int octave_height ;         /**< current octave height. */

    int block_width;			/**< block width, 128 */
    int maxfilter_width;		/**< max width of the filter. **/

    unsigned char   *data0;         /**< origin gray image data */
    input_type      *data;			/**< current data. */
    char            *buffer;		/**< buffer for the interest point detection */
    gs_type         *block_buffer;
    element_type    *log_response;  /**< point of the LoG response images for extrema detection. */
    sift_pix        *grad;          /**< GSS gradient data. */
    bool            *grad_map;

    FilterUnit * filter;		/**< the block filtering unit */
    node fgaussian_filter;		/**< current frequency Gaussian filter */
    node flog_filter;			/**< current frequency Laplacian filter */

